import React, { useState } from 'react';

interface ExportProps {
  tileStyles: React.CSSProperties;
}

const Export: React.FC<ExportProps> = ({ tileStyles }) => {
  const [copySuccess, setCopySuccess] = useState('');

  const formatCssProperties = (styles: React.CSSProperties): string => {
    return Object.entries(styles)
      .map(([key, value]) => {
        const cssKey = key.replace(/[A-Z]/g, (letter) => `-${letter.toLowerCase()}`);
        return `  ${cssKey}: ${value};`;
      })
      .join('\n');
  };

  const cssOutput = `.tile {\n${formatCssProperties(tileStyles)}\n}`;

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(cssOutput);
      setCopySuccess('Copied!');
      setTimeout(() => setCopySuccess(''), 2000);
    } catch (err) {
      setCopySuccess('Failed to copy.');
      setTimeout(() => setCopySuccess(''), 2000);
    }
  };

  const downloadCssFile = () => {
    const blob = new Blob([cssOutput], { type: 'text/css' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'tile.css';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="export-section">
      <h3>Export</h3>
      <div className="export-preview-container">
        <div className="export-preview" style={tileStyles}></div>
      </div>
      <div className="export-code">
        <pre>{cssOutput}</pre>
      </div>
      <div className="export-actions">
        <button onClick={copyToClipboard}>Copy to Clipboard</button>
        {copySuccess && <span>{copySuccess}</span>}
        <button onClick={downloadCssFile}>Download as .css file</button>
      </div>
    </div>
  );
};

export default Export;
