import React, { useRef } from 'react';
import AttributePanel, { AttributePanelRef } from '../AttributePanel/AttributePanel';
import Preview from '../Preview/Preview';

interface TextureCreatorProps {
  tileStyles: React.CSSProperties;
  setTileStyles: React.Dispatch<React.SetStateAction<React.CSSProperties>>;
}

const TextureCreator: React.FC<TextureCreatorProps> = ({ tileStyles, setTileStyles }) => {
  const attributePanelRef = useRef<AttributePanelRef>(null);

  const handleRandomize = () => {
    attributePanelRef.current?.randomize();
  };

  return (
    <div className="texture-creator">
      <h2>Texture Creator</h2>
      <Preview tileStyles={tileStyles} />
      <div className="controls">
        <button onClick={handleRandomize}>Randomize</button>
      </div>
      <AttributePanel ref={attributePanelRef} setTileStyles={setTileStyles} />
    </div>
  );
};

export default TextureCreator;
