import React from 'react';

interface AttributeBoxProps {
  attribute: { id: number; type: string; value: any };
  onRemove: () => void;
  onUpdate: (newValue: any) => void;
}

const AttributeBox: React.FC<AttributeBoxProps> = ({ attribute, onRemove, onUpdate }) => {
  const handleBackgroundColorChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    onUpdate(e.target.value);
  };

  const handleLinearGradientChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    onUpdate({ ...attribute.value, [name]: value });
  };

  const handleRadialGradientChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    onUpdate({ ...attribute.value, [name]: value });
  };

  const renderAttributeControls = () => {
    switch (attribute.type) {
      case 'background-color':
        return (
          <input
            type="color"
            value={attribute.value}
            onChange={handleBackgroundColorChange}
          />
        );
      case 'linear-gradient':
        return (
          <div>
            <label>Color 1:</label>
            <input
              type="color"
              name="color1"
              value={attribute.value.color1}
              onChange={handleLinearGradientChange}
            />
            <label>Color 2:</label>
            <input
              type="color"
              name="color2"
              value={attribute.value.color2}
              onChange={handleLinearGradientChange}
            />
            <label>Angle:</label>
            <input
              type="range"
              name="angle"
              min="0"
              max="360"
              value={attribute.value.angle}
              onChange={handleLinearGradientChange}
            />
            <span>{attribute.value.angle}Â°</span>
          </div>
        );
      case 'radial-gradient':
        return (
          <div>
            <label>Color 1:</label>
            <input
              type="color"
              name="color1"
              value={attribute.value.color1}
              onChange={handleRadialGradientChange}
            />
            <label>Color 2:</label>
            <input
              type="color"
              name="color2"
              value={attribute.value.color2}
              onChange={handleRadialGradientChange}
            />
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="attribute-box">
      <div className="attribute-box-header">
        <span>{attribute.type.replace('-', ' ')}</span>
        <button onClick={onRemove}>X</button>
      </div>
      <div className="attribute-box-controls">{renderAttributeControls()}</div>
    </div>
  );
};

export default AttributeBox;
