export interface CssAttribute {
  id: string; // Used for state and maps to a CSS property (camelCase)
  label: string; // Display name in the UI
  category: string; // For grouping in the dropdown
  controlType: 'color' | 'slider' | 'select' | 'multi' | 'checkbox';
  defaultValue: any;
  // For 'select'
  options?: { value: string; label: string }[];
  // For 'slider'
  min?: number;
  max?: number;
  step?: number;
  // For 'multi'
  subAttributes?: Omit<CssAttribute, 'category' | 'subAttributes'>[];
}

// A master list of all supported CSS attributes for the dropdown.
export const CSS_ATTRIBUTES: CssAttribute[] = [
  {
    id: 'backgroundColor',
    label: 'Background Color',
    category: 'Background',
    controlType: 'color',
    defaultValue: '#ffffff',
  },
  {
    id: 'linear-gradient',
    label: 'Linear Gradient',
    category: 'Background',
    controlType: 'multi', // Special case, contributes to backgroundImage
    defaultValue: { angle: 90, color1: '#FFFFFF', color2: '#000000' },
    subAttributes: [
      { id: 'angle', label: 'Angle', controlType: 'slider', min: 0, max: 360, defaultValue: 90, step: 1 },
      { id: 'color1', label: 'Color 1', controlType: 'color', defaultValue: '#FFFFFF' },
      { id: 'color2', label: 'Color 2', controlType: 'color', defaultValue: '#000000' },
    ],
  },
  {
    id: 'radial-gradient',
    label: 'Radial Gradient',
    category: 'Background',
    controlType: 'multi', // Special case, contributes to backgroundImage
    defaultValue: { color1: '#FFFFFF', color2: '#000000' },
    subAttributes: [
      { id: 'color1', label: 'Color 1', controlType: 'color', defaultValue: '#FFFFFF' },
      { id: 'color2', label: 'Color 2', controlType: 'color', defaultValue: '#000000' },
    ],
  },
  {
    id: 'border',
    label: 'Border',
    category: 'Border',
    controlType: 'multi',
    defaultValue: { width: 1, style: 'solid', color: '#000000' },
    subAttributes: [
      { id: 'width', label: 'Width (px)', controlType: 'slider', min: 0, max: 50, step: 1, defaultValue: 1 },
      {
        id: 'style',
        label: 'Style',
        controlType: 'select',
        defaultValue: 'solid',
        options: [
          { value: 'none', label: 'None' },
          { value: 'solid', label: 'Solid' },
          { value: 'dashed', label: 'Dashed' },
          { value: 'dotted', label: 'Dotted' },
          { value: 'double', label: 'Double' },
          { value: 'groove', label: 'Groove' },
          { value: 'ridge', label: 'Ridge' },
          { value: 'inset', label: 'Inset' },
          { value: 'outset', label: 'Outset' },
        ],
      },
      { id: 'color', label: 'Color', controlType: 'color', defaultValue: '#000000' },
    ],
  },
  {
    id: 'borderRadius',
    label: 'Border Radius (px)',
    category: 'Border',
    controlType: 'slider',
    defaultValue: 0,
    min: 0,
    max: 100,
    step: 1,
  },
  {
    id: 'boxShadow',
    label: 'Box Shadow',
    category: 'Effects',
    controlType: 'multi',
    defaultValue: {
      offsetX: 2,
      offsetY: 2,
      blurRadius: 5,
      spreadRadius: 0,
      color: '#00000080',
      inset: false,
    },
    subAttributes: [
      { id: 'offsetX', label: 'X Offset (px)', controlType: 'slider', min: -50, max: 50, step: 1, defaultValue: 2 },
      { id: 'offsetY', label: 'Y Offset (px)', controlType: 'slider', min: -50, max: 50, step: 1, defaultValue: 2 },
      { id: 'blurRadius', label: 'Blur (px)', controlType: 'slider', min: 0, max: 100, step: 1, defaultValue: 5 },
      { id: 'spreadRadius', label: 'Spread (px)', controlType: 'slider', min: -50, max: 50, step: 1, defaultValue: 0 },
      { id: 'color', label: 'Color', controlType: 'color', defaultValue: '#00000080' },
      { id: 'inset', label: 'Inset', controlType: 'checkbox', defaultValue: false },
    ],
  },
  {
    id: 'transform',
    label: 'Transform',
    category: 'Effects',
    controlType: 'multi',
    defaultValue: {
      rotate: 0,
      scaleX: 1,
      scaleY: 1,
    },
    subAttributes: [
      { id: 'rotate', label: 'Rotate (deg)', controlType: 'slider', min: 0, max: 360, step: 1, defaultValue: 0 },
      { id: 'scaleX', label: 'Scale X', controlType: 'slider', min: 0, max: 3, step: 0.1, defaultValue: 1 },
      { id: 'scaleY', label: 'Scale Y', controlType: 'slider', min: 0, max: 3, step: 0.1, defaultValue: 1 },
    ],
  },
  {
    id: 'opacity',
    label: 'Opacity',
    category: 'Effects',
    controlType: 'slider',
    defaultValue: 1,
    min: 0,
    max: 1,
    step: 0.05,
  }
];
