import React, { useState, useEffect, useRef } from 'react';
import { TileStyle } from '../../data/types';

type GridCell = number | null;

interface SandboxProps {
  palette: TileStyle[];
  grid: GridCell[][];
  setGrid: React.Dispatch<React.SetStateAction<GridCell[][]>>;
  paintCell: (row: number, col: number) => void;
  clearGrid: () => void;
  randomizeGrid: () => void;
}

const MIN_GRID_SIZE = 16;
const MAX_GRID_SIZE = 64;

const Sandbox: React.FC<SandboxProps> = ({
  palette,
  grid,
  setGrid,
  paintCell,
  clearGrid,
  randomizeGrid,
}) => {
  const [isPainting, setIsPainting] = useState(false);
  const [zoom, setZoom] = useState(1); // 1x, 2x, 3x
  const [showGridLines, setShowGridLines] = useState(true);
  const [gridDimensions, setGridDimensions] = useState({ rows: 32, cols: 32 });
  const gridContainerRef = useRef<HTMLDivElement>(null);

  const cellSize = 12 * zoom;

  useEffect(() => {
    const resizeObserver = new ResizeObserver(entries => {
      for (let entry of entries) {
        const { width, height } = entry.contentRect;
        const newCols = Math.max(MIN_GRID_SIZE, Math.min(MAX_GRID_SIZE, Math.floor(width / (cellSize + 1))));
        const newRows = Math.max(MIN_GRID_SIZE, Math.min(MAX_GRID_SIZE, Math.floor(height / (cellSize + 1))));

        setGridDimensions({ rows: newRows, cols: newCols });
      }
    });

    if (gridContainerRef.current) {
      resizeObserver.observe(gridContainerRef.current);
    }

    return () => resizeObserver.disconnect();
  }, [cellSize]);

  useEffect(() => {
    setGrid(currentGrid => {
      const newGrid: GridCell[][] = Array.from({ length: gridDimensions.rows }, () => Array(gridDimensions.cols).fill(null));
      for (let i = 0; i < Math.min(currentGrid.length, gridDimensions.rows); i++) {
        for (let j = 0; j < Math.min(currentGrid[i].length, gridDimensions.cols); j++) {
          newGrid[i][j] = currentGrid[i][j];
        }
      }
      return newGrid;
    });
  }, [gridDimensions, setGrid]);

  // Mouse event handlers
  const handleMouseDown = (row: number, col: number) => {
    setIsPainting(true);
    paintCell(row, col);
  };

  const handleMouseEnter = (row: number, col: number) => {
    if (isPainting) paintCell(row, col);
  };

  const handleMouseUp = () => setIsPainting(false);
  const handleMouseLeave = () => setIsPainting(false);

  const getCellStyle = (rowIndex: number, cellIndex: number): React.CSSProperties => {
    const paletteIndex = grid[rowIndex]?.[cellIndex];
    const baseStyle: React.CSSProperties = {
      width: `${cellSize}px`,
      height: `${cellSize}px`,
    };

    if (paletteIndex === null || paletteIndex === undefined) {
      return { ...baseStyle, backgroundColor: '#ddd' };
    }

    const tileStyle = palette[paletteIndex];
    return { ...baseStyle, ...tileStyle };
  };

  return (
    <div className="sandbox">
      <div className="sandbox-controls">
        <button onClick={clearGrid}>Clear</button>
        <button onClick={randomizeGrid}>Randomize</button>
        <button onClick={() => setShowGridLines(!showGridLines)} className={showGridLines ? 'active' : ''}>
          Grid
        </button>
        <div className="zoom-controls">
          <button onClick={() => setZoom(1)} className={zoom === 1 ? 'active' : ''}>1x</button>
          <button onClick={() => setZoom(2)} className={zoom === 2 ? 'active' : ''}>2x</button>
          <button onClick={() => setZoom(3)} className={zoom === 3 ? 'active' : ''}>3x</button>
        </div>
        <div className="grid-dimensions">{`${gridDimensions.rows} × ${gridDimensions.cols}`}</div>
      </div>
      <div 
        ref={gridContainerRef}
        className={`sandbox-grid ${!showGridLines ? 'no-grid' : ''}`}
        style={{
          gridTemplateColumns: `repeat(${gridDimensions.cols}, ${cellSize}px)`,
          gridTemplateRows: `repeat(${gridDimensions.rows}, ${cellSize}px)`,
        }}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseLeave}
      >
        {grid.map((row, rowIndex) =>
          row.map((_, cellIndex) => (
            <div
              key={`${rowIndex}-${cellIndex}`}
              className="grid-cell"
              onMouseDown={() => handleMouseDown(rowIndex, cellIndex)}
              onMouseEnter={() => handleMouseEnter(rowIndex, cellIndex)}
              style={getCellStyle(rowIndex, cellIndex)}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default Sandbox;
